mapper.select:
		 select manager_id,manager_name,manager_password,manager_call
		 from manager 
		 where 1=1
             and manager_id=#{arg,jdbcType=VARCHAR}
             and manager_name like #{key,jdbcType=VARCHAR}
		 order by manager_name



		SELECT r.manager_id,r.manager_name,r.manager_password,r.manager_call  
			 FROM (   SELECT tt.*, ROWNUM AS rowno   
			          FROM (   
		                    select  manager_id,manager_name,manager_password,manager_call 
		                    from manager 
		                    where 1=1
		                     and manager_id=#{params.arg,jdbcType=VARCHAR}
		                     and ( params.manager_name like #{params.key,jdbcType=VARCHAR}
		                    order by manager_name
		                    )tt   
			                WHERE #{pageSize}*#{pageNo} >=ROWNUM   
			                ) r
			   WHERE r.rowno >= #{pageSize}*(#{pageNo}-1) +1

		   SELECT max(rownum)
        FROM manager
        where 1=1
           and manager_id=#{params.arg,jdbcType=VARCHAR}
           and ( manager_name like #{params.key,jdbcType=VARCHAR}

