mapper.select:
select * from contract_product_c
where 1=1
 and CONTRACT_ID=#{contractId}

mapper.update:
update contract_product_c
CONTRACT_ID=#{contractId}, FACTORY_ID=#{factoryId}, FACTORY_NAME=#{factoryName}, PRODUCT_NO=#{productNo}, PRODUCT_IMAGE=#{productImage}, PRODUCT_DESC=#{productDesc}, CNUMBER=#{cnumber}, OUT_NUMBER=#{outNumber}, LOADING_RATE=#{loadingRate}, BOX_NUM=#{boxNum}, PACKING_UNIT=#{packingUnit}, PRICE=#{price}, AMOUNT=#{amount}, FINISHED=#{finished}, EXTS=#{exts}, ORDER_NO=#{orderNo},
where contract_product_id=#{id}

mapper.delete:
delete from contract_product_c
where contract_product_id in

	#{id}

delete from contract_product_c
where CONTRACT_ID in

	#{id}
