mapper.select:
		SELECT  r.class_id, r.class_name,r.coach,r.coach_call,r.college_id,r.grade
			 FROM (   SELECT tt.*, ROWNUM AS rowno   
			          FROM (   
		                    select class_id,class_name,coach,coach_call,college_id,grade
		                    from classes
		                    where 1=1
				                and grade = #{params.arg,jdbcType=VARCHAR}
		 						and ( class_name like #{params.key,jdbcType=VARCHAR} 
			 													  or coach like #{params.key,jdbcType=VARCHAR} 
			 													  or coach_call like #{params.key,jdbcType=VARCHAR} ) 
		                    order by class_name
		                    )tt   
			                WHERE #{pageSize}*#{pageNo} >=ROWNUM   
			                ) r
				WHERE r.rowno >= #{pageSize}*(#{pageNo}-1) +1

   		   SELECT max(rownum)
           FROM classes
           where 1=1
            and grade = #{params.arg,jdbcType=VARCHAR}
			and ( class_name like #{params.key,jdbcType=VARCHAR} 
											  or coach like #{params.key,jdbcType=VARCHAR} 
											  or coach_call like #{params.key,jdbcType=VARCHAR} )

mapper.update:
update classes
class_name=#{className,jdbcType=VARCHAR}, coach=#{coach,jdbcType=VARCHAR}, coach_call=#{coachCall,jdbcType=VARCHAR}, college_id=#{collegeId,jdbcType=VARCHAR}, grade=#{grade,jdbcType=VARCHAR},
where class_id=#{classId,jdbcType=VARCHAR}

mapper.delete:
delete from classes
where class_id in

	#{id}
