mapper.select:
		 select student_id,student_name,student_password,sex,class_id,class_name,status,bedroom_id,bedroom_name,student_no,grade,college_id,college_name
		 from student 
		 where 1=1
            
             and bedroom_id=#{bedroomId,jdbcType=VARCHAR}
            
             and class_id=#{classId,jdbcType=VARCHAR}
             and college_id=#{collegeId,jdbcType=VARCHAR}
            
             and status=#{status,jdbcType=VARCHAR}
             and college_id=#{college,jdbcType=VARCHAR}
             and grade=#{grade,jdbcType=VARCHAR}
             and sex=#{sex,jdbcType=VARCHAR}

             and student_no=#{name,jdbcType=VARCHAR}
             and student_password=#{password,jdbcType=VARCHAR}

            
            	 	and student_name like #{key,jdbcType=VARCHAR}
             


		 
		 order by class_name

	
	
			SELECT  r.student_id,r.student_name,r.sex,r.class_id,r.class_name,r.status,r.bedroom_id  ,r.student_password,
					r.bedroom_name,r.student_no,r.grade,r.college_id,r.college_name
				 FROM (   SELECT tt.*, ROWNUM AS rowno   
				          FROM (   
			                    select student_id,student_name,sex,class_id,class_name,student_password,status,bedroom_id,bedroom_name,student_no,grade,college_id,college_name
			                    from student 
			                    where 1=1
								 and bedroom_id=#{params.bedroomId,jdbcType=VARCHAR}
            
									
			                     and status=#{params.status,jdbcType=VARCHAR}
					             and college_id=#{params.college,jdbcType=VARCHAR}
					             and grade=#{params.grade,jdbcType=VARCHAR}
					             and sex=#{params.sex,jdbcType=VARCHAR}
					
								                    
			                    
			                     and class_name=#{params.arg,jdbcType=VARCHAR}
			                     and ( student_name like #{params.key,jdbcType=VARCHAR} 
			                    								or  class_name like #{params.key,jdbcType=VARCHAR}   
			                    								or  student_no like #{params.key,jdbcType=VARCHAR}   
			                    								or bedroom_name like #{params.key,jdbcType=VARCHAR} )
			                    order by class_name
				                 
			                    )tt   
				                WHERE #{pageSize}*#{pageNo} >=ROWNUM   
				                ) r
				   WHERE r.rowno >= #{pageSize}*(#{pageNo}-1) +1

   		   SELECT max(rownum)
           FROM student
           where 1=1
				 and bedroom_id=#{params.bedroomId,jdbcType=VARCHAR}
				
  		         and status=#{params.status,jdbcType=VARCHAR}
	             and college_id=#{params.college,jdbcType=VARCHAR}
	             and grade=#{params.grade,jdbcType=VARCHAR}
	             and sex=#{params.sex,jdbcType=VARCHAR}
		
				and student_name=#{params.name,jdbcType=VARCHAR}
		      	and student_password=#{params.password,jdbcType=VARCHAR}
		      
		
			
                   and arg=#{params.arg,jdbcType=VARCHAR}
                    and ( student_name like #{params.key,jdbcType=VARCHAR} 
                    								or  class_name like #{params.key,jdbcType=VARCHAR}   
                 										or  student_no like #{params.key,jdbcType=VARCHAR}
                    								or bedroom_name like #{params.key,jdbcType=VARCHAR} )

mapper.update:
update student
student_name=#{studentName,jdbcType=VARCHAR}, student_password=#{studentPassword,jdbcType=VARCHAR}, sex=#{sex,jdbcType=VARCHAR}, class_id=#{classId,jdbcType=VARCHAR}, class_name=#{className,jdbcType=VARCHAR}, status=#{status,jdbcType=VARCHAR}, bedroom_id=#{bedroomId,jdbcType=VARCHAR}, bedroom_name=#{bedroomName,jdbcType=VARCHAR}, student_no=#{studentNo,jdbcType=VARCHAR}, college_id=#{collegeId,jdbcType=VARCHAR}, college_name=#{collegeName,jdbcType=VARCHAR}, grade=#{grade,jdbcType=VARCHAR}
where student_id=#{studentId,jdbcType=VARCHAR}

mapper.delete:
delete from student
where student_id in

	#{id}
